    str += '<path d="M8.26 4.68h4.26a.48.48 0 0 1 .28.87L9.35 8.02l1.33 4.01a.48.48 0 0 1-.18.54.48.48 0 0 1-.56 0l-3.44-2.5-3.44 2.5a.48.48 0 0 1-.74-.54l1.33-4L.2 5.54a.48.48 0 0 1 .28-.87h4.26l1.3-4a.48.48 0 0 1 .92 0l1.3 4z">'
